import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { employeeService } from '../services/api';
import { Employee, PaginationInfo } from '../types/employee';
import './Dashboard.css';

const Dashboard: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [pagination, setPagination] = useState<PaginationInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(50);

  useEffect(() => {
    if (user?.role === 'admin' || user?.role === 'supervisor') {
      fetchEmployees(currentPage, pageSize);
    } else {
      // Employees see their profile
      navigate('/profile');
    }
  }, [currentPage, pageSize, user, navigate]);

  const fetchEmployees = async (page: number, size: number) => {
    try {
      setLoading(true);
      setError(null);
      const response = await employeeService.getEmployees(page, size);
      setEmployees(response.data);
      setPagination(response.pagination);
    } catch (err: any) {
      if (err.response?.status === 401 || err.response?.status === 403) {
        setError('Session expired or unauthorized. Please login again.');
        setTimeout(() => logout(), 2000);
      } else {
        setError('Failed to fetch employee data.');
      }
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${process.env.REACT_APP_API_URL}/export/attendance`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) throw new Error('Export failed');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `attendance_${new Date().toISOString().split('T')[0]}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      alert('Failed to export data');
    }
  };

  const getInOutBadge = (mode: number) => {
    if (mode === 0) {
      return <span className="badge badge-in">IN</span>;
    } else if (mode === 1) {
      return <span className="badge badge-out">OUT</span>;
    } else {
      return <span className="badge badge-default">{mode || 'N/A'}</span>;
    }
  };

  const formatDateTime = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString();
    } catch {
      return dateString;
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="header-content">
          <div className="user-info">
            <h1>Welcome, {user?.full_name}!</h1>
            <span className="role-badge">{user?.role.toUpperCase()}</span>
          </div>
          <div className="header-actions">
            {user?.role === 'employee' && (
              <button onClick={() => navigate('/profile')} className="btn btn-primary">
                My Profile
              </button>
            )}
            <button onClick={handleExport} className="btn btn-success">
              📊 Export to Excel
            </button>
            <button onClick={logout} className="btn btn-secondary">
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="stats-bar">
          <div className="stat-item">
            <span className="stat-label">📊 Total Records:</span>
            <span className="stat-value">{pagination?.totalRecords.toLocaleString() || 0}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">📄 Current Page:</span>
            <span className="stat-value">{pagination?.currentPage || 1} / {pagination?.totalPages || 1}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">🏢 Company:</span>
            <span className="stat-value">MSS</span>
          </div>

          <div className="page-size-selector">
            <label htmlFor="pageSize">Records per page:</label>
            <select id="pageSize" value={pageSize} onChange={(e) => { setPageSize(Number(e.target.value)); setCurrentPage(1); }}>
              <option value="25">25</option>
              <option value="50">50</option>
              <option value="100">100</option>
              <option value="200">200</option>
            </select>
          </div>
        </div>

        <div className="table-container">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <h2>Loading employee data...</h2>
            </div>
          ) : error ? (
            <div className="error">
              <h2>⚠️ Error</h2>
              <p>{error}</p>
            </div>
          ) : employees.length === 0 ? (
            <div className="no-data">
              <h2>📭 No Data Found</h2>
              <p>No employee attendance records available.</p>
            </div>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Company</th>
                  <th>Branch</th>
                  <th>Employee Code</th>
                  <th>Name (English)</th>
                  <th>Name (Arabic)</th>
                  <th>Last Name</th>
                  <th>Site (English)</th>
                  <th>Site (Arabic)</th>
                  <th>Clock ID</th>
                  <th>In/Out</th>
                  <th>Punch Time</th>
                </tr>
              </thead>
              <tbody>
                {employees.map((emp, index) => (
                  <tr key={index}>
                    <td>{emp.Company_Code || 'N/A'}</td>
                    <td>{emp.Branch_Code || 'N/A'}</td>
                    <td className="emp-code">{emp.Employee_Code || 'N/A'}</td>
                    <td>{emp.Employee_Name_1_English || 'N/A'}</td>
                    <td>{emp.Employee_Name_1_Arabic || 'N/A'}</td>
                    <td>{emp.first_Last_name_eng || 'N/A'}</td>
                    <td>{emp.Site_1_English || 'N/A'}</td>
                    <td>{emp.Site_1_Arabic || 'N/A'}</td>
                    <td>{emp.clock_id || 'N/A'}</td>
                    <td>{getInOutBadge(emp.InOutMode)}</td>
                    <td>{formatDateTime(emp.punch_time)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        {pagination && !loading && !error && (
          <div className="pagination">
            <button onClick={() => setCurrentPage(currentPage - 1)} disabled={!pagination.hasPreviousPage}>
              ← Previous
            </button>

            <span className="pagination-info">
              Page {pagination.currentPage} of {pagination.totalPages}
            </span>

            <button onClick={() => setCurrentPage(currentPage + 1)} disabled={!pagination.hasNextPage}>
              Next →
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
