import React, { useState, useEffect } from 'react';
import './App.css';
import { employeeService } from './services/api';
import { Employee, PaginationInfo } from './types/employee';

function App() {
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [pagination, setPagination] = useState<PaginationInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(50);
  const [jumpToPage, setJumpToPage] = useState('');

  useEffect(() => {
    fetchEmployees(currentPage, pageSize);
  }, [currentPage, pageSize]);

  const fetchEmployees = async (page: number, size: number) => {
    try {
      setLoading(true);
      setError(null);
      const response = await employeeService.getEmployees(page, size);
      setEmployees(response.data);
      setPagination(response.pagination);
    } catch (err) {
      setError('Failed to fetch employee data. Please check if the API server is running.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handlePreviousPage = () => {
    if (pagination?.hasPreviousPage) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (pagination?.hasNextPage) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePageSizeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setPageSize(Number(e.target.value));
    setCurrentPage(1); // Reset to first page
  };

  const handleJumpToPage = () => {
    const pageNum = parseInt(jumpToPage);
    if (pageNum > 0 && pagination && pageNum <= pagination.totalPages) {
      setCurrentPage(pageNum);
      setJumpToPage('');
    }
  };

  const formatDateTime = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString();
    } catch {
      return dateString;
    }
  };

  const getInOutBadge = (mode: number) => {
    if (mode === 0) {
      return <span className="badge badge-in">IN</span>;
    } else if (mode === 1) {
      return <span className="badge badge-out">OUT</span>;
    } else {
      return <span className="badge badge-default">{mode || 'N/A'}</span>;
    }
  };

  return (
    <div className="app">
      <div className="container">
        <div className="header">
          <h1>👥 Employee Attendance System</h1>
          <p>Real-time attendance tracking with Laserfiche integration</p>
        </div>

        <div className="stats-bar">
          <div className="stats-left">
            <div className="stat-item">
              <span className="stat-label">📊 Total Records:</span>
              <span className="stat-value">{pagination?.totalRecords.toLocaleString() || 0}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">📄 Current Page:</span>
              <span className="stat-value">{pagination?.currentPage || 1} / {pagination?.totalPages || 1}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">🏢 Company:</span>
              <span className="stat-value">MSS</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">🕐 Clock ID:</span>
              <span className="stat-value">3</span>
            </div>
          </div>

          <div className="page-size-selector">
            <label htmlFor="pageSize">Records per page:</label>
            <select id="pageSize" value={pageSize} onChange={handlePageSizeChange}>
              <option value="25">25</option>
              <option value="50">50</option>
              <option value="100">100</option>
              <option value="200">200</option>
            </select>
          </div>
        </div>

        <div className="table-container">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <h2>Loading employee data...</h2>
            </div>
          ) : error ? (
            <div className="error">
              <h2>⚠️ Error</h2>
              <p>{error}</p>
            </div>
          ) : employees.length === 0 ? (
            <div className="no-data">
              <h2>📭 No Data Found</h2>
              <p>No employee attendance records available.</p>
            </div>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Company</th>
                  <th>Branch</th>
                  <th>Employee Code</th>
                  <th>Name (English)</th>
                  <th>Name (Arabic)</th>
                  <th>Last Name</th>
                  <th>Site (English)</th>
                  <th>Site (Arabic)</th>
                  <th>Clock ID</th>
                  <th>In/Out</th>
                  <th>Punch Time</th>
                </tr>
              </thead>
              <tbody>
                {employees.map((emp, index) => (
                  <tr key={index}>
                    <td>{emp.Company_Code || 'N/A'}</td>
                    <td>{emp.Branch_Code || 'N/A'}</td>
                    <td className="emp-code">{emp.Employee_Code || 'N/A'}</td>
                    <td>{emp.Employee_Name_1_English || 'N/A'}</td>
                    <td>{emp.Employee_Name_1_Arabic || 'N/A'}</td>
                    <td>{emp.first_Last_name_eng || 'N/A'}</td>
                    <td>{emp.Site_1_English || 'N/A'}</td>
                    <td>{emp.Site_1_Arabic || 'N/A'}</td>
                    <td>{emp.clock_id || 'N/A'}</td>
                    <td>{getInOutBadge(emp.InOutMode)}</td>
                    <td>{formatDateTime(emp.punch_time)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        {pagination && !loading && !error && (
          <div className="pagination">
            <button onClick={handlePreviousPage} disabled={!pagination.hasPreviousPage}>
              ← Previous
            </button>

            <span className="pagination-info">
              Page {pagination.currentPage} of {pagination.totalPages}
            </span>

            <button onClick={handleNextPage} disabled={!pagination.hasNextPage}>
              Next →
            </button>

            <div className="page-jump">
              <input
                type="number"
                min="1"
                max={pagination.totalPages}
                value={jumpToPage}
                onChange={(e) => setJumpToPage(e.target.value)}
                placeholder="Page"
                onKeyPress={(e) => e.key === 'Enter' && handleJumpToPage()}
              />
              <button onClick={handleJumpToPage}>Go</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
